echo "<!DOCTYPE html><html><head>  <title>OsmAnd Offroad-Survey Plugin Preview (Northern Thailand)</title>  <meta charset='utf-8' />  <meta name='viewport' content='width=device-width, initial-scale=1.0'>    <link rel='stylesheet' href='https://unpkg.com/leaflet@1.7.1/dist/leaflet.css' integrity='sha512-xodZBNTC5n17Xt2atTPuE1HxjVMSvLVW9ocqUKLsCC5CXdbqCmblAshOMAS6/keqq/sMZMZ19scR4PsZChSR7A==' crossorigin='' />    <link rel='stylesheet' href='https://cdn.jsdelivr.net/npm/font-awesome@4.7.0/css/font-awesome.min.css' />    <link rel='stylesheet' href='https://aratcliffe.github.io/Leaflet.contextmenu/dist/leaflet.contextmenu.css' />    <script src='https://unpkg.com/leaflet@1.7.1/dist/leaflet.js' integrity='sha512-XQoYMqMTK8LvdxXYG3nZ448hOEQiglfqkJs1NOQV44cWnUrBc8PkAOcXy20w0vlaXaVUearIOBhiXZ5V3ynxwA==' crossorigin=''></script>    <script src='https://aratcliffe.github.io/Leaflet.contextmenu/dist/leaflet.contextmenu.js'></script>    <script src='https://osmlab.github.io/osm-auth/osmauth.js'></script>    <style>    html,body,#map{width:100%;height:100%;margin: 0; padding: 0;}    #crosshair { left: calc(50% - 10px); top: calc(50% - 10px); position: absolute; width: 20px; height: 20px; z-index: 10000; text-align: center; font-weight: normal; font-size: 32px; color: #222; text-shadow: 1px 1px 3px #fff; }  </style></head><body><div id='map'></div><div id='crosshair'>‚åñ</div><script>    var auth = osmAuth({    oauth_secret: '9WfJnwQxDvvYagx1Ut0tZBsOZ0ZCzAvOje3u1TV0',    oauth_consumer_key: 'WLwXbm6XFMG7WrVnE8enIF6GzyefYIN6oUJSxG65',    auto: true,    singlepage: true,    landing: window.location.href  });  var urlParams = new URLSearchParams(window.location.search);  if(urlParams.has('oauth_token')){    auth.bootstrapToken(urlParams.get('oauth_token'), (error) => {      if(error !== null) {        window.location.href = window.location.origin;      } else         authenticate();    });  }  else {    authenticate()  };  const USERS = [ 'cmoffroad', 'crsCR', 'kellerk', 'Bernhard Hiller', 'Russ McD', 'cdohrman', 'ben-cnx' ];  function authenticate() {    if (auth.authenticated()) {      loadUser();    } else {      auth.authenticate(loadUser);    }  }  function loadUser() {    auth.xhr({      method: 'GET',      path: '/api/0.6/user/details.json'    }, (err, res) => {      let user = undefined;      try {        user = JSON.parse(res).user;      } catch (e) {}      console.log(err, res);      if (err || !user) {        document.querySelector('body').innerHTML = err;      } else if (USERS.indexOf(user.display_name) === -1) {        document.querySelector('body').innerHTML = '<br>&nbsp;&nbsp;&nbsp;&nbsp;Please contact OSM user <a href=https://www.openstreetmap.org/message/new/cmoffroad>cmoffroad</a> to request access.';      } else {        draw();      }    });    }  function logout() {    auth.logout();    window.location.reload();   }  function draw() {    const hash = location.hash || '#map=13/19.20742852680121/98.81103515625';    const tokens = hash.substring(5).split('/');    if (!location.hash) {      history.pushState(null,null, hash);    }    const center = [ tokens[1], tokens[2] ], zoom = tokens[0];    const pluginLayer = L.tileLayer('./tiles/{z}/{x}/{y}.png?date=2022-02-22', {      attribution: 'osmand-offroad-survey-plugin - <a href=https://github.com/cmoffroad/osmand-offroad-survey-plugin>Documentation</a> - <a href="#" onclick=logout()>Logout</a>',      minNativeZoom: 13,      maxNativeZoom: 13    });    var map = new L.Map('map', {       minZoom: 12,       maxZoom: 15,      maxBounds: 0 > 0 && 0 > 0 ? L.latLngBounds(         L.latLng(19.186677697957826, 98.8330078125),         L.latLng(19.186677697957826, 98.8330078125)      ) : undefined,      layers: [ pluginLayer ],      zoomControl: true,      contextmenu: true,      contextmenuWidth: 200,      contextmenuItems: [        {          text: 'Center map here',          callback: (e) => map.panTo(e.latlng)        },        {          text: 'Copy coordinates',          callback: (e) => navigator.clipboard.writeText(e.latlng.lat + ',' + e.latlng.lng)        },        '-',        {          text: 'Edit in OSM (Maxar)',          callback: (e) => window.open('https://www.openstreetmap.org/edit#background=Maxar-Standard&map=16/' + e.latlng.lat + '/' + e.latlng.lng)        },        {          text: 'Edit in OSM (WorldTopoMap)',          callback: (e) => window.open('https://www.openstreetmap.org/edit#background=custom:https://{switch:services,server}.arcgisonline.com/arcgis/rest/services/World_Topo_Map/MapServer/tile/{zoom}/{y}/{x}&map=16/' + e.latlng.lat + '/' + e.latlng.lng)        }            ]    })    .setView(center, 13);    map.attributionControl.setPrefix('');    map.on('moveend', (e) => {      const center = e.target.getCenter(), zoom = e.target.getZoom();      history.pushState(null,null, '#map=' + zoom + '/' + center.lat + '/' + center.lng);          });    var baseLayers = {      'osmand-offroad-survey-plugin': pluginLayer,      'cyclemap': L.tileLayer('https://{s}.tile.thunderforest.com/cycle/{z}/{x}/{y}.png?apikey=6170aad10dfd42a38d4d8c709a536f38'),      'opentopomap': L.tileLayer('https://{s}.tile.opentopomap.org/{z}/{x}/{y}.png'),      'worldtopomap': L.tileLayer('https://services.arcgisonline.com/arcgis/rest/services/World_Topo_Map/MapServer/tile/{z}/{y}/{x}'),      'komoot': L.tileLayer('https://{s}.tile.komoot.de/komoot/{z}/{x}/{y}.png?lang=en'),      'mapy.cz': L.tileLayer('https://mapserver.mapy.cz/base-m/{z}-{x}-{y}?sdk=HgUbCgUbGkgqAQkYBxYEHQNHQlJfR1VfQlBZSw%3D%3D'),      'google (terrain)': L.tileLayer('https://mt3.Google.com/vt?z={z}&x={x}&y={y}&lyrs=p'),      'google (satellite)': L.tileLayer('https://mt3.Google.com/vt?z={z}&x={x}&y={y}&lyrs=y')    };    const hillshadeLayer = L.tileLayer('./tiles/{z}/{x}/{y}-hillshade.png', { minNativeZoom: 12, maxNativeZoom: 12, transparency: true, opacity: 0.2 }).addTo(map);    var overlays = {      'SRTM Hillshade 20%': hillshadeLayer,      'OSM GPS traces': L.tileLayer('https://{s}.gps-tile.openstreetmap.org/lines/{z}/{x}/{y}.png'),      'DRR Dynamic': L.tileLayer.wms('https://gisportal.drr.go.th/portal/sharing/servers/aa5fdea8d96542d49d3d8731d77458b7/rest/services/GISBaseMaps/DRR_Dynamic/MapServer/export?transparent=true&format=png&layers=show:1,2,3,4,5,6,7&bboxSR=102100&f=image')    };        L.control.layers(baseLayers, overlays).addTo(map);  }</script></body></html>" > ../../OSM/osm-tools/osmand-offroad-survey-plugin-preview/index.html
